INSERT INTO public."DanhMuc" ("MaDanhMuc","ID","GiaTri","MoiQuanHe") VALUES
	 ('TOI_DANH','10370','10876-Khi Viện trưởng vắng mặt, một Phó Viện trưởng được Viện trưởng ủy nhiệm thực hiện nhiệm vụ, quyền hạn của Viện trưởng, trừ quyền quyết định kháng nghị quy định tại điểm d khoản 1 Điều này. Phó Viện trưởng chịu trách nhiệm trước Viện trưởng về việc thực hiện nhiệm vụ, quyền hạn được ủy nhiệm.',''),
